import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api', // use your backend URL
});
export const saveFavorite = (id) => API.post(`/user/favorites/${id}`);
export const saveToWatchlist = (id) => API.post(`/user/watchlist/${id}`);
export const getFavorites = () => API.get('/user/favorites');
export const getWatchlist = () => API.get('/user/watchlist');


API.interceptors.request.use((req) => {
  const token = localStorage.getItem('token');
  if (token) {
    req.headers.Authorization = `Bearer ${token}`;
  }
  return req;
});

export default API;
